  `product_id` varchar(40) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关联产品',
  `status` int(0) NULL DEFAULT 1 COMMENT '线索状态0删除|作废1正常',
  PRIMARY KEY (`visit_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '客户拜访' ROW_FORMAT = Dynamic;

